package com.example.mosubookshelfimport android.app.Applicationimport android.os.Bundleimport androidx.activity.ComponentActivityimport androidx.activity.compose.setContentimport androidx.activity.enableEdgeToEdgeimport androidx.compose.foundation.layout.*import androidx.compose.material3.*import androidx.compose.ui.Modifierimport androidx.navigation.compose.*import com.example.mosubookshelf.navigation.MainBottomNavigationimport com.example.mosubookshelf.navigation.MainNavHostimport com.example.mosubookshelf.ui.theme.MosuBookShelfThemeimport dagger.hilt.android.AndroidEntryPointimport dagger.hilt.android.HiltAndroidApp@HiltAndroidAppclass MosuBookShelfApplication : Application()@AndroidEntryPointclass MainActivity : ComponentActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        enableEdgeToEdge()        setContent {            MosuBookShelfTheme {                val navController = rememberNavController()                Scaffold(                    modifier = Modifier.fillMaxSize(),                    bottomBar = {                        MainBottomNavigation(navController = navController)                    },                ) { innerPadding ->                    MainNavHost(                        navController = navController,                        viewModelStoreOwner = this@MainActivity,                        modifier = Modifier.padding(innerPadding)                    )                }            }        }    }}